import { Body, Controller, Get, Post, UploadedFile, UseInterceptors } from '@nestjs/common'
import { FileService } from './file.service'
import { FileInterceptor } from '@nestjs/platform-express'

// ! THIS CONTROLLER FOR TESTING
@Controller('file')
export class FileController {
	constructor(private fileService: FileService) {}
	@Get()
	async test() {
		// ! For testing
		const stringBase64 = `data:image/png;base64,`
		return await this.fileService.uploadBase64(stringBase64)
	}

	@UseInterceptors(FileInterceptor('file'))
	@Post('f')
	async testWithPipe(@UploadedFile() file: Express.Multer.File) {
		return await this.fileService.uploadFile(file)
	}

	@Post()
	async delete(@Body('path') path: string) {
		return this.fileService.remove(path)
	}
}
